import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, Image,ScrollView } from 'react-native';

// const products = [
//     { id: 1, name: 'Product 1', price: '100.000đ', image: require('../assets/ip15.jpeg') },
//     { id: 2, name: 'Product 2', price: '200.000đ', image: require('../assets/ip15.jpeg') },
//     { id: 3, name: 'Product 3', price: '300.000đ', image: require('../assets/ip15.jpeg') },
// ];



function Home() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        getAllProduct();
    }, []);

    const getAllProduct = () => {
        axios
            .get('https://fakestoreapi.com/products')
            .then(function (response) {
                // handle success
                setProducts(response.data);
            })
            .catch(function (error) {
                // handle error
                alert(error.message);
            })
            .finally(function () {
                // always executed
                console.log('Finally called');
            });
    };
    const renderProduct = (product) => (
        <View style={styles.productItem} key={product.id}>
            <Image source={{ uri: product.image }} style={styles.productImage} resizeMode='cover'/>
            <Text style={styles.productName}>{product.title}</Text>
            <Text style={styles.productPrice}>{product.price}$</Text>
            <Text style={styles.description}>{product.description}</Text>

        </View>
    );
    return (
        <View style={styles.container}>
            <Text style={styles.title}>Shop công thương</Text>
            <Image source={require('../images/logo.png')} style={styles.logo} />
            <Text style={styles.title}>Tất cả sản phẩm</Text>
            <ScrollView vertical>
                {products.map((product) => renderProduct(product))}
            </ScrollView>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 16,
       
    },
    title: {
        fontSize: 24,
        fontWeight: 'bold',
        marginBottom: 5,
        borderColor: 'red',
        borderWidth: 1,
        backgroundColor: 'pink'
    },
    logo: {
        width: '100%',
        height: 200,
        marginBottom: 16,
        resizeMode: 'cover',
        borderColor: 'pink',
        borderWidth: 1,

    },
    productItem: {
        alignItems: 'center',
        marginRight: 16,
        marginBottom: 5,
        borderColor: 'pink',
        borderWidth: 1,
        flex: 5
      },
      productImage: {
        width: 150,
        height: 150,
        resizeMode: 'cover',
        marginBottom: 8,
        borderRadius: 4,
        marginTop: 5
        
      },
      productName: {
        fontSize: 16,
        fontWeight: 'bold',
        marginBottom: 4,
      },
      productPrice: {
        fontSize: 24,
        fontWeight: 'bold',
        marginLeft: 2,
        color: 'green',
        
      },
      description: {
        fontSize: 12,
        lineHeight: 24,
        color: '#333',
        borderRadius: 2,
        marginBottom: 10,
        marginLeft: 5,
        marginRight: 5
      },
});

export default Home;
